(function (e, t, n) {
    "use strict";
   
    e.sceditor = function (o, r) {
        $(o).hide();

        var nt = this;

        if ($(o).hasClass('sceditor')) {
            return o;
        }
        else {

            $(o).addClass('sceditor');

            $(o).after('<div class="sceditor-container ltr"><iframe frameborder="0" style="height:100px;"></iframe></div>');

            $('iframe').contents().find('body').attr('contenteditable', true);

            $('iframe').contents().find('body').css({ 'font-family': 'Segoe UI', 'font-size': '13px' });
        }

       //$('iframe').contents().find('head').append('<link rel="stylesheet" type="text/css" href="/Scripts/ref/editor/jquery.sceditor.default.min.css">');
    },
    e.fn.sceditor = function (t) {
      return e.sceditor(this, t);
    }
})(jQuery, window, document);

jQuery.fn.extend({
    getBody: function () {
        var result = new Array();
        $(this).each(function (index, item) { result.push($(item).next().find('iframe').contents().find('body').get(0)); });
        return result;
    }
});